import React, { useState } from "react";
import GenerateFromVideo from "./components/GenerateFromVideo";
import GLBGrid from "./components/GLBGrid";
import Scene from "./Scene";

const App: React.FC = () => {
  const [currentGLBId, setCurrentGLBId] = useState<number>(0);
  const [currentGLBName, setCurrentGLBName] = useState<string>("base.glb");
  const [refreshCounter, setRefreshCounter] = useState(0);

  const triggerRefresh = () => setRefreshCounter(prev => prev + 1);

  const glbUrl = `http://127.0.0.1:8000/transform/rig?id=${currentGLBId}&name=${currentGLBName}`;
  const videoUrl = currentGLBId ? `http://127.0.0.1:8000/video/${currentGLBId}` : "";

  return (
    <div className="bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white min-h-screen">
      <header className="text-center py-6 border-b border-gray-700">
        <h1 className="text-4xl font-extrabold tracking-tight bg-gradient-to-r from-blue-400 to-teal-300 bg-clip-text text-transparent">
          Texel Art Animation Maker
        </h1>
      </header>

      <main className="flex flex-col md:flex-row">
        {/* Left: Grid */}
        <section className="md:w-1/2 bg-gray-100 text-black overflow-auto border-r border-gray-300">
          <GLBGrid
            onSelectGLB={(file) => {
              setCurrentGLBId(file.id);
              setCurrentGLBName(file.name);
            }}
            refreshTrigger={refreshCounter}
          />
        </section>

        {/* Right: Video + GLB */}
        <section className="md:w-1/2 flex flex-col bg-gray-900 p-4 gap-4">
          <div className="flex-1 flex flex-col md:flex-row gap-4">
            {/* GLB */}
            <div className="flex-1 bg-gray-800 rounded-2xl shadow-lg p-3">
              {currentGLBId ? <Scene url={glbUrl} /> : <p>Select an animation</p>}
            </div>

            {/* Video */}
            <div className="flex-1 bg-gray-800 rounded-2xl shadow-lg p-3">
              {currentGLBId ? (
                <video
                  src={videoUrl}
                  controls
                  autoPlay
                  loop
                  className="w-full h-full rounded-xl"
                />
              ) : (
                <p>Select an animation</p>
              )}
            </div>
          </div>

          {/* Generate Video Button */}
          <div className="flex-none">
            <GenerateFromVideo setGLBUrl={() => {}} triggerGLBRefresh={triggerRefresh} />
          </div>
        </section>
      </main>
    </div>
  );
};

export default App;
