import {
  Html,
  OrbitControls,
  Stats,
  useAnimations,
  useProgress,
} from "@react-three/drei";
import { Canvas, useLoader } from "@react-three/fiber";
import React, { Suspense, useEffect, useRef } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";

function Loader() {
  const { progress } = useProgress();
  return <Html center>{progress.toFixed(0)} % loaded</Html>;
}

interface ModelWithAnimationProps {
  url: string;
}

function ModelWithAnimation({ url }: ModelWithAnimationProps) {
  const gltf = useLoader(GLTFLoader, url);
  const modelRef = useRef<THREE.Group>(null);
  const { animations } = gltf;
  const { actions } = useAnimations(animations, modelRef);

  useEffect(() => {
    // Play animation safely
    if (actions && animations.length > 0) {
      const actionName = animations[0].name;
      actions[actionName]?.reset().play();
    }

    return () => {
      // Cleanup: stop animations & dispose geometry/material
      Object.values(actions || {}).forEach((action) => action.stop());
      gltf.scene.traverse((child: any) => {
        if (child.isMesh) {
          child.geometry.dispose();
          if (Array.isArray(child.material)) {
            child.material.forEach((m) => m.dispose());
          } else {
            child.material.dispose();
          }
        }
      });
    };
  }, [actions, animations, gltf.scene]);

  return <primitive object={gltf.scene} ref={modelRef} position={[0, 0, 0]} />;
}

interface SceneProps {
  url: string;
  preview?: boolean; // ðŸ‘ˆ new prop for grid thumbnails
}

const Scene: React.FC<SceneProps> = ({ url, preview = false }) => {
  return (
    <Canvas
      camera={{ position: [1.3, 1.5, 1.9] }}
      shadows
      style={{
        height: preview ? 150 : "100%",
        width: "100%",
      }}
    >
      <Suspense fallback={<Loader />}>
        <directionalLight
          position={[-1.3, 6.0, 4.4]}
          castShadow
          intensity={Math.PI}
        />
        <ambientLight intensity={0.4} />
        <ModelWithAnimation url={url} />
        {!preview && <OrbitControls target={[0, 1, 0]} />}
        {!preview && <axesHelper args={[5]} />}
        {!preview && <Stats />}
      </Suspense>
    </Canvas>
  );
};

export default Scene;
