import React, { useEffect, useState } from "react";

interface GLBGridProps {
  onSelectGLB: (url: string) => void;
  refreshTrigger: number; // increment to trigger refetch
}

interface GLBItem {
  name: string;
  url: string;
}

const GLBGrid: React.FC<GLBGridProps> = ({ onSelectGLB, refreshTrigger }) => {
  const [glbList, setGlbList] = useState<GLBItem[]>([]);

  const fetchGLBs = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/joints/");
      const data = await res.json();
      setGlbList(data);
    } catch (err) {
      console.error("Failed to fetch GLBs:", err);
    }
  };

  useEffect(() => {
    fetchGLBs();
  }, [refreshTrigger]); // refetch whenever refreshTrigger changes

  return (
    <div className="grid grid-cols-2 gap-4 p-4">
      {glbList.map((glb) => (
        <div
          key={glb.name}
          className="bg-gray-200 p-2 rounded cursor-pointer hover:bg-gray-300"
          onClick={() => onSelectGLB(glb.url)}
        >
          {glb.name}
        </div>
      ))}
    </div>
  );
};

export default GLBGrid;
