import React, { useEffect, useState } from "react";
import "./Scene";
import Scene from "./Scene";
import GenerateFromVideo from "./components/GenerateFromVideo";

// Helper Function: extract first-frame thumbnail from a videoBlob
async function generateVideoThumbnail(videoBlob: Blob): Promise<string> {
  return new Promise((resolve) => {
    const video = document.createElement("video");
    video.src = URL.createObjectURL(videoBlob);
    video.muted = true;
    video.playsInline = true;

    video.addEventListener("loadeddata", () => {
      video.currentTime = 0.1;
    });

    video.addEventListener("seeked", () => {
      const canvas = document.createElement("canvas");
      canvas.width = 180;
      canvas.height = 120;

      const ctx = canvas.getContext("2d");
      if (ctx) {
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
        const dataUrl = canvas.toDataURL("image/jpeg");
        resolve(dataUrl);
      } else {
        resolve("");
      }
    });
  });
}

const App: React.FC = () => {
  const [currentGLBUrl, setCurrentGLBUrl] = useState<string>("/models/base.glb");
  const [currentVideoUrl, setCurrentVideoUrl] = useState<string>("");
  const [refreshCounter, setRefreshCounter] = useState(0);

  const [files, setFiles] = useState<any[]>([]);
  const [loading, setLoading] = useState<boolean>(true);  // ⬅ NEW

  const triggerRefresh = () => setRefreshCounter(prev => prev + 1);

  useEffect(() => {
    async function loadFiles() {
      try {
        const res = await fetch("http://127.0.0.1:8000/joints");
        const metadata = await res.json();

        const enriched = await Promise.all(
          metadata.map(async (file: { id: number; name: string }) => {
            const [glbRes, videoRes] = await Promise.all([
              fetch(`http://127.0.0.1:8000/transform/rig?id=${file.id}&name=${file.name}`),
              fetch(`http://127.0.0.1:8000/video/${file.id}`)
            ]);

            const glbBlob = await glbRes.blob();
            const videoBlob = await videoRes.blob();

            const thumb = await generateVideoThumbnail(videoBlob);

            return {
              ...file,
              glbBlob,
              videoBlob,
              thumbnail: thumb
            };
          })
        );

        setFiles(enriched);
      } catch (err) {
        console.error("Error loading animation files:", err);
        setFiles([]);
      }
      setLoading(false); // ⬅ TURN OFF LOADING WHEN DONE
    }

    loadFiles();
  }, []);


  return (
    <div className="bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white min-h-screen">
      <header className="text-center py-6 border-b border-gray-700">
        <h1 className="text-4xl font-extrabold tracking-tight bg-gradient-to-r from-blue-400 to-teal-300 bg-clip-text text-transparent">
          Texel Art Animation Maker
        </h1>
      </header>

      <main className="flex flex-col md:flex-row">
        <section className="md:w-1/2 bg-gray-100 text-black overflow-auto border-r border-gray-300">

          {/* ---------------- LOADING STATE ---------------- */}
          {loading ? (
            <div className="flex justify-center items-center py-10">
              <div className="animate-spin h-10 w-10 border-4 border-blue-500 border-t-transparent rounded-full"></div>
            </div>
          ) : (
            <div className="p-6 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6">
              {files.map((file) => {
                const glbObjectUrl = URL.createObjectURL(file.glbBlob);
                const videoObjectUrl = URL.createObjectURL(file.videoBlob);

                return (
                  <div
                    key={file.id}
                    className="cursor-pointer p-2 rounded-lg border border-gray-300 hover:bg-gray-200 transition"
                    onClick={() => {
                      setCurrentGLBUrl(glbObjectUrl);
                      setCurrentVideoUrl(videoObjectUrl);
                    }}
                  >
                    <p className="text-xs break-words text-center">{file.name}</p>
                  </div>
                );
              })}

              {/* NO ANIMATIONS */}
              {!loading && files.length === 0 && (
                <p className="col-span-full text-center text-gray-500">
                  No animations found.
                </p>
              )}
            </div>
          )}

        </section>

        {/* ---------------- RIGHT SIDE ---------------- */}
        <section className="md:w-1/2 flex flex-col bg-gray-900 p-4 space-y-4">
          <div className="flex-1 flex flex-col md:flex-row gap-4">
            <div className="flex-1 bg-gray-800 rounded-2xl shadow-lg p-3">
              <div style={{ maxHeight: '200px' }}>
                {currentVideoUrl && (
                  <video autoPlay loop>
                    <source src={currentVideoUrl} type="video/mp4" />
                  </video>
                )}
              </div>
              <div><Scene url={currentGLBUrl} /></div>
            </div>
          </div>

          <div className="flex-none">
            <GenerateFromVideo
              setGLBUrl={setCurrentGLBUrl}
              triggerGLBRefresh={triggerRefresh}
            />
          </div>
        </section>
      </main>
    </div>
  );
};

export default App;
