import { FormEvent } from "react";

interface GenerateFromVideoProps {
  triggerGLBRefresh: () => void;
}

const GenerateFromVideo: React.FC<GenerateFromVideoProps> = ({ triggerGLBRefresh }) => {
  // ... existing state, file upload, animationName, status, error, loading

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError(""); 
    setStatus(""); 
    setLoading(true);

    // validation checks...
    const formData = new FormData();
    formData.append("file", videoFile!);
    formData.append("name", animationName);

    try {
      const res = await fetch("http://127.0.0.1:8000/process/video/", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();

      if (res.ok) {
        setStatus(`✅ Animation saved as: ${data.name}`);
        triggerGLBRefresh(); // refresh GLBGrid
      } else {
        setError(data.error || "Something went wrong.");
      }
    } catch {
      setError("Upload failed. Server error.");
    } finally {
      setLoading(false);
    }
  };

  // ... return JSX (with loading, error, status)
};
